in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform color{
  vec4 imgColor;
};
uniform Properties {
  float radius;
};
void main () {
  vec4 accum = vec4(0.0);
  vec4 normal = vec4(0.0);
  normal = texture2D(texture, vec2(v_uv0.x, v_uv0.y));
  accum += texture2D(texture, vec2(v_uv0.x - radius, v_uv0.y - radius));
